    "Course_Des_3" : "!yf! Familiar with this game genre.",
    "Course_DesAward_18" : "!yg! 400",
    "Course_DesAward_19" : "!yh! 400",
    "Course_DesAward_20" : "!yi! 400",
    "Course_DesAward_21" : "!yj! 400",
    "Course_DesAward_22" : "!yk! 400",
