    "Technology_Name_ID_4905" : "!702! Elite Army Camp",
    "Technology_Content_4905" : "!703! Unlock buildings: <color=#df840e>Elite Shield Camp</color>, <color=#df840e>Elite Archery Camp</color>. ",
    "Technology_Name_ID_4906" : "!704! Quality Military Supplies",
    "Technology_Content_4906" : "!705! Unlock the recipe for <color=#df840e>Quality Military Supplies</color> at Advanced Smithy. Unlock recipes for <color=#df840e>Steel Shields</color>, <color=#df840e>Steel Bows</color>, and <color=#df840e>Steel Spears</color> at Advanced Smithy.",
    "Technology_Name_4906_1" : "!706! Quality Military Supplies",
    "Technology_Content_4906_1" : "!707! Unlock the recipe for <color=#df840e>Quality Military Supplies</color> at Advanced Smithy.",
